// ============================================
// 헬퍼 함수들 (job_matcher.py와 동일)
// ============================================

function _parseEmploymentTypes(employmentStr) {
  /** 고용형태 문자열 파싱 */
  if (!employmentStr) return [];
  return employmentStr.split(",").map(emp => emp.trim()).filter(emp => emp);
}

function _simpleMatch(jobField, targetJob) {
  /** 단순 문자열 매칭 */
  if (!jobField || !targetJob) return false;
  return targetJob.toLowerCase().replace(/\s/g, "") 
    .includes(jobField.toLowerCase().replace(/\s/g, "")) ||
    jobField.toLowerCase().replace(/\s/g, "")
    .includes(targetJob.toLowerCase().replace(/\s/g, ""));
}

function _isJobMatch(job, targetJobs) {
  /** 직무 매칭 여부 판단 */
  if (!targetJobs || targetJobs.length === 0) return true;

  const processedPosition = job.processed_position_name || "";
  for (const targetJob of targetJobs) {
    if (!targetJob) continue;
    if (processedPosition && processedPosition !== "미분류") {
      if (_simpleMatch(processedPosition, targetJob)) {
        return true;
      }
    }
  }
  return false;
}

function _isEducationMatch(userEduList, jobEducation) {
  for (const userEdu of userEduList) {
    // 양방향 포함 체크
    if (jobEducation.includes(userEdu) || userEdu.includes(jobEducation)) {
      return true;
    }

    // 학사 4년 특수 케이스
    if ((userEdu.includes("학사") && userEdu.includes("4년")) &&
        (jobEducation.includes("대학") && jobEducation.includes("4년"))) {
      return true;
    }
  }
  return false;
}

function isPreferredCompany(targetCompanies, jobCompany) {
  /** 희망기업 여부 확인 */
  if (!targetCompanies || targetCompanies.length === 0 || !jobCompany) {
    return false;
  }

  // 1. 직접 매칭
  if (targetCompanies.includes(jobCompany)) return true;

  // 2. 매핑 테이블을 통한 매칭
  for (const targetCompany of targetCompanies) {
    let mappedNames = companyNameMapping[targetCompany] || [];
    if (typeof mappedNames === "string") mappedNames = [mappedNames];

    for (const mappedName of mappedNames) {
      if (mappedName === jobCompany) return true;
      // 부분 매칭
      if (mappedName.toLowerCase().includes(jobCompany.toLowerCase()) ||
          jobCompany.toLowerCase().includes(mappedName.toLowerCase())) {
        return true;
      }
    }
  }

  // 3. 기본 부분 문자열 매칭
  const jobCompanyClean = jobCompany.toLowerCase().replace(/[\s\/]/g, "");
  for (const targetCompany of targetCompanies) {
    const targetClean = targetCompany.toLowerCase().replace(/[\s\/]/g, "");
    if (targetClean.includes(jobCompanyClean) || jobCompanyClean.includes(targetClean)) {
      return true;
    }

    if (jobCompany.includes("/")) {
      const companyBase = jobCompany.split("/")[0].toLowerCase().replace(/\s/g, "");
      if (targetClean.includes(companyBase) || companyBase.includes(targetClean)) {
        return true;
      }
    }
  }

  return false;
}

function countNulls(jobDict) {
  /** 채용공고의 NULL 개수 계산 */
  const checkFields = ["min_education_level", "min_experience_level", "employment_type"];
  return checkFields.filter(field => 
    !jobDict[field] || jobDict[field] === "확인불가"
  ).length;
}

function getJobRolePriority(targetJobs, job) {
  /** 직무 우선순위 계산 (낮을수록 우선순위 높음) */
  for (let i = 0; i < targetJobs.length; i++) {
    if (targetJobs[i] && _isJobMatch(job, [targetJobs[i]])) {
      return i + 1;
    }
  }
  return 5;
}

function getPriority(userValues, jobValue) {
  /** 공통 우선순위 계산 함수 */
  if (!jobValue || jobValue === "확인불가") return 2;
  if (!userValues || userValues.length === 0) return 3;
  return userValues.includes(jobValue) ? 1 : 3;
}

function _extractTargetCompanies(userData) {
  /** 사용자가 관심있는 회사들 추출 */
  const companies = [];

  for (const [groupName, groupInfo] of Object.entries(companyMappings)) {
    const groupSelection = userData[groupInfo.key] || "";
    if (groupSelection && groupSelection.trim()) {
      if (groupSelection.includes("전체")) {
        companies.push(...groupInfo.companies);
        console.log(`'${groupName} 전체' 선택됨 → ${groupInfo.companies.length}개 회사 추가`);
      } else {
        const selected = groupSelection.split(",").map(c => c.trim()).filter(c => c);
        companies.push(...selected);
        console.log(`'${groupName}' 개별 선택 → ${selected}`);
      }
    }
  }

  return [...new Set(companies)]; // 중복 제거
}

