// 필터링 함수들 (job_matcher.py와 동일)
// ============================================

function applyBasicFilters(userData, jobPostings) {
  /** 1단계 필터링: 신입 공고만 추출 */
  const filteredJobs = [];
  const careerPreference = userData["찾고 계신 공고의 경력 조건을 선택해주세요."] || "";

  for (const job of jobPostings) {
    const experienceLevel = job.min_experience_level || "";
    if (careerPreference.includes("신입")) {
      if (["신입", "확인불가", ""].includes(experienceLevel)) {
        filteredJobs.push(job);
      }
    } else {
      filteredJobs.push(job);
    }
  }
  return filteredJobs;
}

function filterByEmploymentType(userData, jobPostings) {
  /** 2단계 필터링: 고용형태 */
  const filteredJobs = [];
  const targetEmploymentTypes = _parseEmploymentTypes(
    userData["희망 고용 형태 (복수선택)"] || ""
  );

  for (const job of jobPostings) {
    const employmentType = job.employment_type || "";
    if (!employmentType ||
        employmentType === "확인불가" ||
        targetEmploymentTypes.length === 0 ||
        targetEmploymentTypes.includes(employmentType)) {
      filteredJobs.push(job);
    }
  }
  return filteredJobs;
}

function filterByJobRole(userData, jobPostings) {
  /** 3단계 필터링: 직무 */
  const filteredJobs = [];
  let targetJobs = [
    userData["희망 직무 1순위 (필수응답)"] || "",
    userData["희망 직무 2순위 "] || "",  // 공백 포함!
    userData["희망 직무 3순위 "] || "",  // 공백 포함!
  ];
  targetJobs = targetJobs.map(job => job.trim()).filter(job => job);

  for (const job of jobPostings) {
    if (targetJobs.length === 0 || _isJobMatch(job, targetJobs)) {
      filteredJobs.push(job);
    }
  }
  return filteredJobs;
}

function filterByEducation(userData, jobPostings) {
  /** 4단계 필터링: 학력 */
  const filteredJobs = [];
  const userEducation = userData["찾고 계신 공고의 학력 조건을 선택해주세요. (졸업예정자도 선택 가능, 복수선택)"] || "";
  const userEduList = userEducation.split(",").map(edu => edu.trim()).filter(edu => edu);

  for (const job of jobPostings) {
    const education = job.min_education_level || "";
    if (!education ||
        education === "학력_확인불가" ||
        userEduList.length === 0 ||
        _isEducationMatch(userEduList, education)) {
      filteredJobs.push(job);
    }
  }
  return filteredJobs;
}

