function matchJobsForUser(userData, jobData, topN = 100) {
  /** 사용자에게 맞는 채용공고 매칭 */
  console.log("=== 필터링 시작 ===");

  // 사용자 선호도 추출
  const targetCompanies = _extractTargetCompanies(userData);
  let targetJobs = [
    userData["희망 직무 1순위 (필수응답)"] || "",
    userData["희망 직무 2순위 "] || "",
    userData["희망 직무 3순위 "] || "",
  ];
  targetJobs = targetJobs.map(job => job.trim()).filter(job => job);
  const targetEmploymentTypes = _parseEmploymentTypes(
    userData["희망 고용 형태 (복수선택)"] || ""
  );

  console.log(`사용자 희망 직무: ${targetJobs}`);
  console.log(`사용자 희망 회사: ${targetCompanies}`);
  console.log(`사용자 희망 고용형태: ${targetEmploymentTypes}`);

  // 단계별 필터링
  const step1Jobs = applyBasicFilters(userData, jobData);
  console.log(`1단계(경력) 후: ${step1Jobs.length}개 공고`);

  const step2Jobs = filterByEmploymentType(userData, step1Jobs);
  console.log(`2단계(고용형태) 후: ${step2Jobs.length}개 공고`);

  const step3Jobs = filterByJobRole(userData, step2Jobs);
  console.log(`3단계(직무) 후: ${step3Jobs.length}개 공고`);

  const finalJobs = filterByEducation(userData, step3Jobs);
  console.log(`4단계(학력) 후: ${finalJobs.length}개 공고`);

  if (finalJobs.length === 0) {
    console.log("필터링 후 추천할 공고가 없습니다");
    return [];
  }

  // 희망기업 분리
  const preferredJobs = [];
  const otherJobs = [];

  for (const job of finalJobs) {
    const companyName = job.company_name || job.company_name_from_file || "";
    const isPreferred = isPreferredCompany(targetCompanies, companyName);

    if (isPreferred) {
      preferredJobs.push(job);
    } else {
      otherJobs.push(job);
    }
  }

  console.log(`희망기업 공고: ${preferredJobs.length}개, 기타 공고: ${otherJobs.length}개`);

  // 정렬용 키 함수
  const userEducation = userData["찾고 계신 공고의 학력 조건을 선택해주세요. (졸업예정자도 선택 가능, 복수선택)"] || "";
  const userEduList = userEducation.split(",").map(edu => edu.trim()).filter(edu => edu);

  function sortKey(job) {
    return [
      countNulls(job),
      getJobRolePriority(targetJobs, job),
      getPriority(userEduList, job.min_education_level || ""),
      getPriority(targetEmploymentTypes, job.employment_type || ""),
      job.company_name || ""
    ];
  }

  // 튜플 비교 정렬 함수
  function compareSortKeys(a, b) {
    const keyA = sortKey(a);
    const keyB = sortKey(b);
    for (let i = 0; i < keyA.length; i++) {
      if (keyA[i] < keyB[i]) return -1;
      if (keyA[i] > keyB[i]) return 1;
    }
    return 0;
  }

  preferredJobs.sort(compareSortKeys);
  otherJobs.sort(compareSortKeys);
  const topJobs = [...preferredJobs, ...otherJobs].slice(0, topN);

  console.log(`추천 완료: ${topJobs.length}개 공고 선정`);

  return topJobs.map(job => ({
    job: job,
    is_preferred_company: isPreferredCompany(
      targetCompanies,
      job.company_name || job.company_name_from_file || ""
    ),
    score: 100
  }));
}

