    <div style="font-size:14px;margin-bottom:4px;"><b>모집부문</b>: ${positionName}</div>
    <div style="font-size:14px;margin-bottom:4px;"><b>직무명</b>: ${processedPosition}</div>
    <div style="font-size:14px;margin-bottom:10px;"><b>마감</b>: ${deadline}</div>
    <a href="${applyUrl}" target="_blank" style="display:block;background:#f9fafc;border:1px solid #e3e8ef;padding:12px;text-align:center;text-decoration:none;color:#2e2e2e;border-radius:8px;">바로가기</a>
  </td></tr>
</table>`;
}

const results = [];

for (const item of matchedData) {
  const userData = item.json;
  const userName = userData.user_name || "구독자";
  const matchedJobs = userData.matched_jobs || [];
  const jobCardsHtml = matchedJobs.slice(0, 10).map(j => generateJobCard(j)).join("\n");

  // 전체 HTML을 여기서 완성!
  const fullHtml = `<!DOCTYPE html>
<html>
<body style="margin:0;padding:0;background-color:#f4f4f4;">
  <table width="600" style="background:#fff;margin:0 auto;">
    <tr><td style="background:#00822c;padding:25px;color:#fff;">
      <div style="color:#ffc93e;font-size:22px;font-weight:bold;">🎯 채용공고 도착!</div>
    </td></tr>
    <tr><td style="padding:20px;">
      <div style="margin-bottom:20px;">${userName}님에게 맞는 채용공고</div>
      ${jobCardsHtml}
    </td></tr>
    <tr><td style="background:#00822c;padding:20px;text-align:center;color:#fff;">
      JOB FINDER
    </td></tr>
  </table>
</body>
</html>`;

  results.push({
    json: {
      to: userData.user_email,
      subject: `[JOB FINDER] ${userName}님 맞춤 채용공고 ${matchedJobs.length}건`,
      html: fullHtml
    }
  });
}

return results;
